INSERT INTO `baiviet` (`id_bv`, `id_cmpb`, `link_description`, `heading`, `summarize`, `content`, `d_poss`, `view`, `id_user`, `file`, `img`, `location`, `capacity`, `status`, `censor`) VALUES
(49, 51, 'NHUNG-CAM-NHAN-KHOA-DAO-TAO-CAN-BO-QUAN-LY', 'NHỮNG CẢM NHẬN KHÓA ĐÀO TẠO CÁN BỘ QUẢN LÝ ', '<i><b>Anh Vũ Duy Hoạt, Cán bộ quản lý thuộc Công ty Điện lực Đồng Nai phát biểu những suy nghĩ của mình sau khóa đào tạo ngắn hạn CEO tại trường.</b></i><br>', '<p class=\"MsoNormal\" style=\"margin-top:0cm;margin-right:13.5pt;margin-bottom:\n0cm;margin-left:0cm;margin-bottom:.0001pt;text-align:justify;text-indent:36.0pt;\nline-height:normal\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size: 14pt;\">Hôm\nnay, trong không khí long trọng của buổi lể bế giảng khóa học Cán bộ quản lý lớp\ncấp 4 chương trình 1, tôi đại diện cho 39 học viên, xin được phát biểu đôi lời\ncảm tưởng.</span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p class=\"MsoNormal\" style=\"margin-top:0cm;margin-right:13.5pt;margin-bottom:\n0cm;margin-left:0cm;margin-bottom:.0001pt;text-align:justify;text-indent:36.0pt;\nline-height:normal\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size: 14pt;\">Lời\nđầu tiên thay mặt<span style=\"mso-spacerun:yes\">&nbsp; </span>tập thể lớp xin gửi đến\nquý vị đại biểu và quý thầy cô <a name=\"_GoBack\"></a>giáo lời cảm ơn sâu sắc,\nkính chúc quý vị mạnh khỏe, hạnh phúc và thành đạt.</span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p class=\"MsoNormal\" style=\"margin-top:0cm;margin-right:13.5pt;margin-bottom:\n0cm;margin-left:0cm;margin-bottom:.0001pt;text-align:justify;text-indent:36.0pt;\nline-height:normal\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size: 14pt;\">&nbsp;Sau\n5 tuần học tập từ những khuôn mặt lạ lẫm bước vào lớp học đến nay đã trở thành\nnhững người bạn thân quen – gắn bó nhau và rồi hôm nay chúng ta ngồi lại để nhận\nđược kết quả qua thời gian học tập và nói lời tạm biệt, vì thế hôm nay sẽ là một\nngày đáng nhớ trong cuộc đời của mỗi học viên chúng em.</span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p class=\"MsoNormal\" style=\"margin-top:0cm;margin-right:13.5pt;margin-bottom:\n0cm;margin-left:0cm;margin-bottom:.0001pt;text-align:justify;text-indent:36.0pt;\nline-height:normal\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size: 14pt;\">Trong\nsuốt 5 tuần qua, với 20 chuyên đề, chúng em đã được học hỏi những kiến thức, những\nkinh nghiệm quý báu và những điều hết sức tốt đẹp từ các Thầy Cô, đọng lại\ntrong mỗi học viên chính là sự tận tâm và lòng nhiệt huyết với nghề nghiệp, với\nhọc viên của thầy cô. Nhân đây chúng em xin gửi những tình cảm và lời cám ơn\nchân thành nhất để tỏ lòng kính trọng và biết ơn sâu sắc về những việc làm đầy\ný nghĩa mà thầy cô đã dành cho chúng em.</span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p class=\"MsoNormal\" style=\"margin-top:0cm;margin-right:13.5pt;margin-bottom:\n0cm;margin-left:0cm;margin-bottom:.0001pt;text-align:justify;text-indent:36.0pt;\nline-height:normal\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size: 14pt;\">&nbsp;</span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p class=\"MsoNormal\" style=\"margin-top:0cm;margin-right:13.5pt;margin-bottom:\n0cm;margin-left:0cm;margin-bottom:.0001pt;text-align:justify;text-indent:36.0pt;\nline-height:normal\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size: 14pt;\">Có\nthể nói lớp học của chúng em là một tập thể gồm 39 gương mặt được đến từ các\ncông ty của 19 tỉnh thành và 2 đơn vị là Công ty Công nghệ thông tin và Ban quản\nlý dự án lưới điện miền Nam thuộc Tổng Công ty, <span style=\"mso-spacerun:yes\">&nbsp;</span>tuy khác nhau về nghề nghiệp, công việc, chênh\nlệch về độ tuổi và điều kiện công tác, ít nhiều có ảnh hưởng đến việc học tập.\nNhưng vượt lên tất cả, tập thể đã hợp nên một “ tổ ấm ” rất thân thương, rất\nđoàn kết. Trong học tập có những chủ đề thảo luận trên lớp với không khí diễn\nra rất cởi mở, các nhóm tranh luận sôi nổi, vui vẻ, để lại nhiều ấn tượng. Ngoài\nviệc tích cực học tập tri thức, các buổi giao lưu do lớp tổ chức vẫn còn vang vọng\nđâu đây. Nhờ đó lớp đã học hỏi ở nhau nhiều kinh nghiệm quý báu, hiểu nhau hơn,\ngắn bó hơn . Cũng từ đó đã tạo được hứng thú rất lớn hỗ trợ tốt cho công tác học\ntập của lớp. Tuy nhiên trong học tập chúng em cũng không tránh khỏi những thiếu\nsót, kính mong các thầy cô, và Nhà Trường thông cảm và lượng thứ.</span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p style=\"margin-right:13.5pt;text-align:justify;text-indent:36.0pt\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size:14.0pt\">Những cảm nhận sâu sắc, &nbsp;khó quên về hình ảnh\nnhững thầy cô giáo luôn biết cách làm mới mình bằng sự trau dồi tri thức;\nphương pháp, bằng sự thanh cao của tâm hồn, bằng lòng nhiệt tình, sự tận tuỵ và\ntrên hết là một cái tâm trong sáng; hình ảnh đó mãi lung linh trong tâm thức của\nmỗi học viên chúng em hôm qua, hôm nay và mãi sau này. Hình ảnh đáng kính nhưng\nđầy thân thiện của thầy cô như thầy Nguyễn Thanh Hội, thầy Thái Trí Dũng, thầy\nHuỳnh Văn Thông, cô Nguyễn Thị Uyên Uyên, cô Vũ Thi Phượng, cô Lê Thị Trúc\nAnh…, đã tạo được ấn tượng sâu sắc đối với các học viên, là những giá trị nhân\nvăn sâu sắc - &nbsp;Đó là “Dạy cách học và học cách học”. Chính đó đã tạo được\nsự gắn kết giữa học viên với Thầy Cô, làm cho các Học viên chúng em phải tự ý\nthức trong quá trình học tập.</span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-indent:36.0pt\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size:14.0pt\">Qua các chuyên đề Quản trị sản xuất, Quản trị Tài\nchinh, Quản trị nguồn Nhân lực, Văn hóa doanh nghiệp, Quản trị truyền thông, Kỹ\nnăng giao tiếp và dịch vụ khách hàng, … , đã làm cho tất cả các thành viên\ntrong lớp đều thấy rất bổ ích. Những kiến thức của Thầy, Cô truyền đạt lại rất\nthực tế và có tính áp dụng cao. </span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\njustify;text-indent:36.0pt;line-height:normal;tab-stops:130.5pt\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size: 14pt;\">&nbsp;Không chỉ\ncó thế, sau khi kết thúc các chuyên đề, những kiến thức đó đã khắc sâu vào tâm\ntrí của tôi và các bạn và soi lại vào những việc mình đang làm: Làm thế nào để\ntuyển được một nhân viên \"phù hợp\", làm thế nào để lãnh đạo, khiến\nngười nhân viên muốn theo mình, chứ không phải là bắt người ta theo mình, <span style=\"mso-bidi-font-style:italic\">nâng cao hơn những kỹ năng cần thiết để phục\nphụ khách hàng, </span></span><span style=\"font-size: 14pt;\">kỹ năng\nchăm sóc khách hàng có tính chuyên nghiệp hơn trong công việc khai thác khách\nhàng và chăm sóc khách hàng.</span><span style=\"font-size: 14pt;\"></span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\njustify;text-indent:36.0pt;line-height:normal\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size: 14pt;\">&nbsp;</span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\njustify;text-indent:36.0pt;line-height:normal\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size: 14pt;\">Trước khi đến\nvới khóa học này chúng em <span style=\"mso-spacerun:yes\">&nbsp;</span>nghĩ đây cũng\nchỉ thông thường như<span style=\"mso-spacerun:yes\">&nbsp; </span>những chương trình\nkhác. Tuy nhiên sau khi tham gia vào khóa học, chúng em nhận thấy có rất nhiều\nsự khác biệt. </span><span style=\"font-size: 14pt;\">Chương\ntrình học được thiết kế với thời lượng học trên lớp ngắn, cô đọng những kiến thức\ncần thiết, và những hạng mục bài tập nhóm, bài thuyết trình đa dạng để khuyến\nkhích khả năng tự nghiên cứu, tự học hỏi, phối họp nhóm, tự phát triển bản thân\nđã cho các học viên sân chơi học thuật hết sức bổ ích. <span style=\"mso-bidi-font-style:\nitalic\">Khóa học đã mang lại rất nhiều kinh nghiêm quý báu cho anh chị em tham\ndự trong những ngày học vừa qua, mặc dù thời gian học tập ngắn ngủi nhưng phong\ncách giảng dạy và khâu tổ chức của đơn vị đào tạo làm chúng tôi rất hài lòng,\nhy vọng sẽ được tham gia nhiều khóa học hơn nữa do EVNSPC tổ chức.\"</span></span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p style=\"margin-right:13.5pt;text-align:justify;text-indent:36.0pt\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size:14.0pt\"><span style=\"mso-spacerun:yes\">&nbsp;</span>Trong niềm vui\nhân hoan của buổi Lễ bế giảng hôm nay vô cùng vinh dự khi được nhận giấy chứng\nnhận. Đây là kết quả của sự phấn đấu sau 5 tuần học tập, nghiên cứu của các bạn;\nlà nhờ sự tận tình giảng dạy, hướng dẫn của các thầy, cô và sự quan tâm giúp đỡ\ncủa Lãnh đạo Tổng Công ty, của các cán bộ quản lý các Ban Tổng Công ty và Nhà\nTrường. Tuy vậy, chúng em vẫn luôn ý thức rằng “Kiến thức trong thời đại hội\nnhập là không có giới hạn và luôn được cập nhật từng ngày, phải tiếp tục học\nthêm nhiều hơn nữa, từ công việc, từ trong cuộc sống”. Đây chỉ là sự khởi đầu\nquan trọng trong một chặng đường mới, là một động lực to lớn để các anh chi học\nviên tiếp tục phát huy, vận dụng sáng tạo kiến thức đã học vào nhiệm vụ công\ntác của mình để có thể đáp ứng trước những yêu cầu ngày càng cao của thực tế. <em><span style=\"font-style:normal\"></span></em></span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p style=\"margin-right:13.5pt;text-align:justify;text-indent:36.0pt\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size:14.0pt\">Ngày mai đây với những chặng đường phía trước mỗi\nchúng em có thể sẽ thành công, cũng có thể sẽ gặp nhiều khó khăn, thách thức.\nNhưng chúng em xin hứa với Ban lãnh đạo Tổng Công ty, Ban Giám hiệu nhà trường,\nQuý thầy cô giáo sẽ đem hết tâm lực, trí lực đã được đúc kết qua quá trình học\ntập, nghiên cứu, vận dụng một cách linh hoạt và sáng tạo vào thực tiễn để chứng\nminh và khẳng định với xã hội rằng: “Sản phẩm nguồn nhân lực mà Các thầy, các\ncô cung cấp cho chúng em là sản phẩm có chất lượng tốt – Tốt về nhân cách đạo\nđức – Tốt về tư duy kiến thức – Tốt về kỹ năng tác nghiệp chuyên môn, đặc biệt\nlà những giá trị nhân văn ” và chúng em nghĩ rằng đó cũng là một cách để tri ân\nvới Lãnh đạo Tổng công ty và các Thầy cô.</span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p style=\"margin-right:13.5pt;text-align:justify;text-indent:36.0pt\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size:14.0pt\"><span style=\"mso-spacerun:yes\">&nbsp;</span>Tại buổi Lễ hôm\nnay, bằng sự kính trọng và tri ân sâu sắc - Một lần nữa thay mặt cho 39 học\nviên<span style=\"mso-spacerun:yes\">&nbsp; </span>xin trân trọng cảm ơn: Ban lãnh đạo\nTổng Công ty Điện lực Miền Nam, Ban TN&amp;NS Tổng Công ty trực tiếp là Đồng\nchí Nguyễn Tấn Nghiệp Phó trưởng Ban TC&amp; NS, xin cám ơn Quý thầy, cô, Ban\ngiám hiệu, khoa đào tạo nâng cao, các phòng, ban cùng các cán bộ, nhân viên\nTrường cao đẳng Điện lực TP. Hồ Chí Minh đã luôn đồng hành, hỗ trợ và không\nngừng động viên khích lệ; cùng nhiều kỉ niệm đẹp dành cho chúng em. </span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p style=\"margin-right:13.5pt;text-align:justify;text-indent:36.0pt\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size:14.0pt\">Cám ơn anh Nguyễn Hoàng Lực đã dựng cho lớp một video\nclip vừa là vật kỷ niệm vừa là những thông tin cho các bạn để sử dung khi trở\nvề đơn vị. Cám ơn các bạn đã hỗ trợ và giúp đỡ ban cán sự lớp hoàn thành tốt\nnhiệm vụ được giao.</span></font></p><div align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\">\n\n</font></div><p class=\"MsoNormal\" style=\"margin-top:0cm;margin-right:13.5pt;margin-bottom:\n0cm;margin-left:0cm;margin-bottom:.0001pt;text-align:justify;text-indent:36.0pt;\nline-height:normal\" align=\"justify\"><font face=\"Comic Sans MS\" size=\"3\"><span style=\"font-size:14.0pt\">Xin kính chúc Lãnh đạo Tổng Công ty, Ban Giám hiệu;\nQuý vị đại biểu, Quý thầy cô giáo luôn dồi dào sức khỏe, hạnh phúc và thành\nđạt. Chúc toàn thể các anh chị luôn mạnh khỏe, hạnh phúc, thành đạt và thực\nhiện được những ước mơ, hoài bão của mình trong tương lai.&nbsp;&nbsp; &nbsp;\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><em><span style=\"font-size:14.0pt;font-style:normal;\nmso-bidi-font-style:italic\">&nbsp; ​Xin trân trọng cảm ơn!</span></em></font></p><p class=\"MsoNormal\" style=\"margin-top:0cm;margin-right:13.5pt;margin-bottom:\n0cm;margin-left:0cm;margin-bottom:.0001pt;text-align:justify;text-indent:36.0pt;\nline-height:normal\" align=\"justify\"><em><span style=\"font-size:14.0pt;font-style:normal;\nmso-bidi-font-style:italic\"><font size=\"3\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>&nbsp;&nbsp;<font color=\"#660033\">&nbsp; Vũ Duy Hoạt</font></b><